function do_talk(answer)
if (answer=="ask") then
	havetask =0
	local havetask0=0	--默认没有
	local havetask1=0	--感叹号
	local havetask2=0	--黄色问号
	--local havetask3=0	--灰色菜单
	local havetask4=0	--灰色问号
	local level= LuaQuery("level")
	AddTopSayMenuItem("@7寄卖","") 
	--AddTopSayMenuItem("@5数量","ceshi")
	AddTopSayMenuItem("@5[月会员]整理","zlwp")
	AddTopSayMenuItem("@5[月会员]回收","ybjm")
	AddTopSayMenuItem("@5寄卖","npc_list")
	AddTopSayMenuItem("@7获取收益","take_money")
	AddTopSayMenuItem("@7获取超时物品","timeout_agent")
	AddTopSayMenuItem("@7寄卖说明","shuoming")
	if(LuaQueryTask("Tcoc")==13)then
		AddTopSayMenuItem("@2商贾向背","Tcoc")
	end
	--AddTopSayMenuItem("@3小店关门","buy1")
	--AddTopSayMenuItem("@3寄卖元宝","buy2")
	--AddTopSayMenuItem("@7获取收益","getagentmoney")
	--AddTopSayMenuItem("@7获取超时物品","gettimeoutagent")
	--AddTopSayMenuItem("@7寄卖说明","shuoming")
	     
--以上为无符号部分
if(havetask2==1)then
	havetask=2
elseif(havetask4==1)then
	havetask=4
elseif(havetask1==1)then
	havetask=1
else--(包括了havetask0=1)
	havetask=0
end
------------------------------------------------
if(havetask ==2)then
	AddTopSayMenuItem("聊天","liaotian","2")
elseif(havetask ==1) then
	AddTopSayMenuItem("聊天","liaotian","1")
else	
