            LuaAddYB(1,666,"moyulibaoh")
            LuaNotice("恭喜@2"..LuaQueryStr("name").."@0回收@1魔玉大礼包@0@2[极]@0*1获得@3666元宝!")
        else
            LuaSay("回收失败!")
        end
    else
