        DelItem("o_ycrwjz",lnum,"qlycrwjz")
        LuaSay("清理完成")
    else
        LuaSay("无该清理物品!")
    end
elseif(answer=="qlzb")then
    local totalTb=0
    local tcount=0
    for i = 1, #zblist do
        local lnum= LuaItemCountNoEquipped(zblist[i][1].."")
        if(lnum>0)then
            for j=1,lnum do
                DelItem(zblist[i][1].."",1,"qlzb")
            end            
            local delnum=LuaItemCountNoEquipped(zblist[i][1].."")
            local nowCount=lnum-delnum  
            if(nowCount>0)then                
                totalTb=totalTb+(nowCount*zblist[i][2])  
                tcount=tcount+lnum
            end
        end
    end
    if(totalTb>0)then
        LuaGive("coin",totalTb*0.1,"qlzb")
    end
    --LuaGive("e_earring123",1)
    --LuaGive("e_earring123",1)
    --LuaGive("e_earring114",1)
    --SendCommand("call=传送!|0|12|3|4|2")
    LuaSay("清理完成"..tcount.."件装备")
