function setItem(s,e,key,estr)
	local maxc=99
	for i=s,e,1 do
		local itemcount=LuaItemStatusNormalCount(key..i..estr)
		DelItem(key..i..estr,itemcount,"zlwp")
		while itemcount>0
		do			
		if(itemcount>=maxc)then			
			--DelItem(key..i..estr,maxc,"zlwp")
			--for j=1,maxc,1 do
			--	DelItem(key..i..estr,1)
			--end
			local gvs=""
			if(estr=="y" or estr=="j")then
				gvs="j"
			end			
			LuaGive(key..i..gvs,maxc,"zlwp")
			itemcount=itemcount-maxc
			--LuaSay(""..key..i..estr)
		else
			--for j=1,itemcount,1 do
			--	DelItem(key..i..estr,1)
			--end
			--DelItem(key..i..estr,itemcount,"zlwp")
			local gvs=""
			if(estr=="y" or estr=="j")then
				gvs="j"
			end	
			LuaGive(key..i..gvs,itemcount,"zlwp")
			itemcount=itemcount-itemcount
			--LuaSay(key..i..estr.." "..itemcount)
		end
		end
		
	end
end
